package exercise2;

public class Calculator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Calculator calc = new Calculator();
	    calc.add();
	    calc.sub();
	    calc.mul();
	    calc.div();
	}
	    void add()
	    {
	    	int Number1=10;
	    	int Number2=20;
	    	int Total=Number1+Number2;
	    	System.out.println("Total is "+Total);
	    
	    }
	    void sub()
	    {
	    	int Number1=10;
	    	int Number2=20;
	    	int Total=Number1-Number2;
	    	System.out.println("Total is "+Total);
	    
	    }
	    void mul()
	    {
	    	int Number1=10;
	    	int Number2=20;
	    	int Total=Number1*Number2;
	    	System.out.println("Total is "+Total);
	    
	    }
	    void div()
	    {
	    	int Number1=100;
	    	int Number2=20;
	    	int Total=Number1/Number2;
	    	System.out.println("Total is "+Total);
	    
	    }
	    }
		
		
		